if gmcp.Char then zgui.vitals.h = tonumber(gmcp.Char.Vitals.hp) zgui.vitals.m = tonumber(gmcp.Char.Vitals.mp) zgui.vitals.e = tonumber(gmcp.Char.Vitals.ep) zgui.vitals.w = tonumber(gmcp.Char.Vitals.wp)
zgui.vitals.maxh = tonumber(gmcp.Char.Vitals.maxhp) zgui.vitals.maxm = tonumber(gmcp.Char.Vitals.maxmp) zgui.vitals.maxe = tonumber(gmcp.Char.Vitals.maxep) zgui.vitals.maxw = tonumber(gmcp.Char.Vitals.maxwp)
---------------------------------------------------------- zgui.statChange() -- If you want to see health and Mana changes in Log window
zgui.vitals.oh = zgui.vitals.h zgui.vitals.om = zgui.vitals.m end end
