function zgui.showHealthbar()
  if tonumber(gmcp.Char.Vitals.hp) > tonumber(gmcp.Char.Vitals.maxhp) then
  	zgui.healthbar.healthbar.front:setStyleSheet([[
		background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #00ff2e, stop: 0.2 #00d423, stop: 0.5 #008a00, stop: 0.7 #005100, stop: 0.9 #001800);
		border-top: 1px black solid;
		border-left: 1px black solid;
		border-bottom: 1px black solid;
		border-radius: 7;
		padding: 3px;
	  ]])  
    zgui.healthbar.healthbar:setValue(100, 100, "<b><center>  "..tonumber(gmcp.Char.Vitals.hp).. "</center></b>")
  elseif tonumber(gmcp.Char.Vitals.hp) <= (tonumber(gmcp.Char.Vitals.maxhp)/2) then
    zgui.healthbar.healthbar:setValue(tonumber(gmcp.Char.Vitals.hp), tonumber(gmcp.Char.Vitals.maxhp), "<b>  "..tonumber(gmcp.Char.Vitals.hp).. "</b>")
    zgui.healthbar.healthbar.front:setStyleSheet([[
		  background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #621616, stop: 0.1 #640a0a, stop: 0.49 #8a0000, stop: 0.5 #560000, stop: 1 #260000);
  		border-top: 1px black solid;
	  	border-left: 1px black solid;
		  border-bottom: 1px black solid;
		  border-radius: 7;
		  padding: 3px;
	  ]])
  else
  	zgui.healthbar.healthbar.front:setStyleSheet([[
		background-color: QLinearGradient( x1: 0, y1: 0, x2: 0, y2: 1, stop: 0 #00ff2e, stop: 0.2 #00d423, stop: 0.5 #008a00, stop: 0.7 #005100, stop: 0.9 #001800);
		border-top: 1px black solid;
		border-left: 1px black solid;
		border-bottom: 1px black solid;
		border-radius: 7;
		padding: 3px;
	  ]])
    zgui.healthbar.healthbar:setValue(tonumber(gmcp.Char.Vitals.hp), tonumber(gmcp.Char.Vitals.maxhp), "<b><center>  "..tonumber(gmcp.Char.Vitals.hp).. "</center></b>")
  end
end
registerAnonymousEventHandler("gmcp.Char.Vitals", "zgui.showHealthbar")

