function ak.prompt.setassign() if gmcp.Char.Status.class == "Bard" then ak.prompt.uniqueset = {"agoraphobia","claustrophobia","dementia","epilepsy","masochism","recklessness","vertigo","confusion", "dizziness", "impatience", "paranoia", "stupidity", "addiction"} elseif gmcp.Char.Status.class == "Sentinel" then ak.prompt.uniqueset = {"hallucinations","dizziness","recklessness","confusion","paranoia","epilepsy","impatience"} elseif gmcp.Char.Status.class == "Occultist" then ak.prompt.uniqueset = {"dementia","stupidity","confusion","hypersomnia","paranoia","hallucinations","impatience","recklessness","masochism","agoraphobia","loneliness","lovers"} else ak.prompt.uniqueset = {} end end function ak.prompt.scoreup() ak.prompt.setassign() ak.prompt.donotgroup = {} ak.prompt.lockconflict = false ak.prompt.currentcolor = ak.prompt.default promptset = {} local donotreprompt = {} if ak.prompt.lockgroup then for i = 1, #ak.prompt.lockpriority do for x = 1, #ak.prompt.locks[ak.prompt.lockpriority[i]] do if affstrack.score[ak.prompt.locks[ak.prompt.lockpriority[i]][x]] > 50 and not ak.prompt.lockconflict then if x == #ak.prompt.locks[ak.prompt.lockpriority[i]] and not table.contains(ak.prompt.donotgroup, ak.prompt.lockpriority[i]) then if table.contains(ak.prompt.colours.lock, ak.prompt.lockpriority[i] ) then table.insert(promptset, ak.prompt.colours.lock[ak.prompt.lockpriority[i]]..""..ak.prompt.lockpriority[i]) ak.prompt.lockconflict = true else table.insert(promptset, ak.prompt.lockpriority[i]) ak.prompt.lockconflict = true end for m = 1, #ak.prompt.locks[ak.prompt.lockpriority[i]] do table.insert(donotreprompt, ak.prompt.locks[ak.prompt.lockpriority[i]][m]) end end elseif not table.contains(ak.prompt.donotgroup, ak.prompt.lockpriority[i]) then table.insert(ak.prompt.donotgroup, ak.prompt.lockpriority[i]) end end end end if ak.prompt.enablecolor then for k,v in pairs(ak.prompt.colours.afflictions) do if #ak.prompt.colours.afflictions[k] > 0 then for i = 1,#ak.prompt.colours.afflictions[k] do if affstrack.score[ak.prompt.colours.afflictions[k][i]] > 1 and convert[ak.prompt.colours.afflictions[k][i]] and not table.contains(ak.prompt.donotrelay, ak.prompt.colours.afflictions[k][i]) and not table.contains(donotreprompt, ak.prompt.colours.afflictions[k][i]) then if affstrack.score[ak.prompt.colours.afflictions[k][i]] == 100 and convert[ak.prompt.colours.afflictions[k][i]] then ak.prompt.currentcolor = ak.prompt.colours.specific[k] table.insert(promptset, ak.prompt.colours.specific[k]..""..ak.prompt.unique(ak.prompt.colours.afflictions[k][i])..""..convert[ak.prompt.colours.afflictions[k][i]]) table.insert(donotreprompt, ak.prompt.colours.afflictions[k][i]) elseif convert[ak.prompt.colours.afflictions[k][i]] then ak.prompt.currentcolor = ak.prompt.colours.specific[k] table.insert(promptset, ak.prompt.colours.specific[k]..""..ak.prompt.unique(ak.prompt.colours.afflictions[k][i])..""..convert[ak.prompt.colours.afflictions[k][i]].."["..math.floor(affstrack.score[ak.prompt.colours.afflictions[k][i]] + 0.5).."]") table.insert(donotreprompt, ak.prompt.colours.afflictions[k][i]) end end end end end end for k,v in pairs(affstrack.score) do if v > 1 and convert[k] and not table.contains(donotreprompt, k)and not table.contains(ak.prompt.donotrelay, k) then if v == 100 and convert[k] then ak.prompt.currentcolor = ak.prompt.default table.insert(promptset, ak.prompt.default..""..ak.prompt.unique(k)..""..convert[k]) elseif convert[k] and not table.contains(ak.prompt.donotrelay, k) then ak.prompt.currentcolor = ak.prompt.default table.insert(promptset, ak.prompt.default..""..ak.prompt.unique(k)..""..convert[k].."["..math.floor(v + 0.5).."]" ) end end end if affstrack.impale then ak.prompt.currentcolor = ak.prompt.default table.insert(promptset, "<cyan>IMPALED") end if ak.track.flown then ak.prompt.currentcolor = ak.prompt.default table.insert(promptset, "<white>FLOWN") end if gmcp.Char.Status.class == "Infernal" or gmcp.Char.Status.class == "Paladin" or gmcp.Char.Status.class == "Runewarden" or gmcp.Char.Status.class == "Magi" or gmcp.Char.Status.class == "Serpent" or gmcp.Char.Status.class == "Bard" then if ak.defs.rebounding then table.insert(promptset,"<blue>Reb") end if affstrack.ferocity and affstrack.ferocity > 0 then table.insert(promptset,"<cyan>[<red>"..affstrack.ferocity.."<cyan>]") end end if gmcp.Char.Status.class == "Occultist" then if ak.occie.aura.mental > 0 or ak.occie.aura.physical > 0 then table.insert(promptset,"<cyan>[<white>"..ak.occie.aura.physical.."/<green>"..ak.occie.aura.mental.."<cyan>]") end end if gmcp.Char.Status.class == "Depthswalker" then local age = ak.age or 0 if age ~= 0 then table.insert(promptset,"<white>[<red>"..ak.age().."<white>]") end end if ak.bleeding and ak.bleeding > 0 then table.insert(promptset,"<cyan>[<red>"..ak.bleeding.."<cyan>]") end if gmcp.Char.Status.class == "Shaman" and ak.shaman.GetFashions then table.insert(promptset,"<cyan>[<red>F<cyan>]") end end

------------------------------------------------- -- Put your Lua functions here. -- -- -- -- Note that you can also use external Scripts -- ------------------------------------------------- ak.enemyprio.salve = ak.enemyprio.salve or {} ak.enemyprio.salve[target] = ak.enemyprio.salve[target] or {}
ak.tieredaffs = {"rightleg 100","rightleg 200","rightleg 300","leftleg 100","leftleg 200","leftleg 300","rightarm 100","rightarm 200","rightarm 300","leftarm 100","leftarm 200","leftarm 300", "head 100","head 200","head 300","torso 100","torso 200","torso 300"} local ignored = {"rightleg","leftleg","rightarm","leftarm","torso","head"} local salvelocations = {"head","legs","arms","torso"}
function ak.enemyprio.applied(salve) if not table.contains(salvelocations,salve) then return end ak.enemyprio.salve[target] = ak.enemyprio.salve[target] or {} local maxinteger = 0 local compsplit = "" local fuck = ak.enemyprio.salve[target] local aff = {} local insertpoint = false if #fuck > 0 then for i = 1, #affstrack.applied[salve] do if affstrack.score[affstrack.applied[salve][i]] > 0 then aff[affstrack.applied[salve][i]] = affstrack.score[affstrack.applied[salve][i]] end end end for k,v in pairs(aff) do if table.contains(fuck, k.." "..v) then table.remove(fuck,table.index_of(fuck, k.." "..v)) end end if #fuck > 0 then for i = 1, #fuck do compsplit = string.split(fuck[i], " ") if affstrack.score[compsplit[1]] == (compsplit[2] + 0) then if insertpoint then for k,v in pairs(aff) do if not table.contains(fuck, k.." "..v) then table.insert(fuck, insertpoint, k.." "..v) end end else for k,v in pairs(aff) do if not table.contains(fuck, k.." "..v) then table.insert(fuck, i, k.." "..v) end end end insertpoint = false else--if ak.enemyprio.notHigherAff(compsplit[1], k) --donothaft hisaffliction insertpoint = i end end end for k,v in pairs(aff) do if not table.contains(fuck, k.." "..v) then table.insert(fuck, k.." "..v) end end for k,v in pairs(affstrack.applied) do --add to table if table.contains(salvelocations, k) then for i = 1, #v do if affstrack.score[v[i]] > 0 and not table.contains(fuck, v[i].." "..affstrack.score[v[i]]) then table.insert(fuck, v[i].." "..affstrack.score[v[i]]) else--already in the table but in the wrong spot / has a score currently
end end end end if ak.debug then cecho("enemysalveprio") display(fuck) end end
function ak.enemyprio.notHigherAff(aff, aff2) local compslit = "" local compslit2 = "" compsplit = string.split(aff, " ") compsplit2 = string.split(aff2, " ") if compsplit[1] == compslit[2] then return false else return true end
end

