function mmp.astroboots() if mmp.game ~= "lusternia" then return end local option = mmp.settings.astrojump mmp.echo("<green>" .. (option and "Set up" or "Removed") .. " <white>Astrojump <green>exits") local exitString = [[script:if gmcp.Room.Info.exits.%s then send("%s", false) else send("astrojump", false) end]] for k, v in pairs(astralExits) do if option then addSpecialExit(v[1], v[2], string.format(exitString, v[3], v[3])) addSpecialExit(v[2], v[1], string.format(exitString, mirror[v[3]], mirror[v[3]])) else removeSpecialExit(v[1], "astrojump") removeSpecialExit(v[2], "astrojump") removeSpecialExit(v[1], string.format(exitString, v[3], v[3])) removeSpecialExit(v[2], string.format(exitString, mirror[v[3]], mirror[v[3]])) end end end
function mmp.wildnodes(create) if mmp.game ~= "lusternia" then return end for k, v in pairs(astralExits) do if create then setExit(v[1], v[2], v[3]) setExit(v[2], v[1], mirrorExits[v[3]]) else setExit(v[1], -1, v[3]) setExit(v[2], -1, mirrorExits[v[3]]) end end end

