harvRoom = harvRoom or 1
harvArea = harvArea or "none"
function harvDir()
	if (harvArea ~= "none") then
		if (harvRoom > #harvArea) then
			sysEcho("Area Cleared")
			harvRoom = 1
			harvArea = "none"
		else
			mmp.customwalkdelay(0)
			expandAlias("goto " .. harvArea[harvRoom])
			harvRoom = harvRoom + 1
		end
	else
		return
	end
end

function sendToDiscordWebhook(discord_username, avatarurl, message) local httpdone = registerAnonymousEventHandler('sysPostHttpDone', function(event, rurl, response) if rurl == url then display(r) else return true end end, true)
local httperror = registerAnonymousEventHandler('sysPostHttpError', function(event, response, rurl) if rurl == url then display(r) else return true end end, true)
local url = "https://discordapp.com/api/webhooks/975391849319641161/xVtJk-By84k1Tm9yR3Or8bdMepMInRSufOuVlkxcByX_IEK7rPHvjClFa0zMArSAah7j" local data = { username = discord_username, avatar_url = avatarurl, content = message } local header = { ["Content-Type"] = "application/json", --["Content-Length"] = data:len() }
postHTTP(yajl.to_string(data), url, header)
killAnonymousEventHandler(httpdone) killAnonymousEventHandler(httperror) end -- -- function sendToDiscordWebhook(discord_username, avatarurl, message) -- local httpdone = registerAnonymousEventHandler('sysPostHttpDone', -- function(event, rurl, response) -- if rurl == url then display(r) else return true end -- end, true) -- -- local httperror = registerAnonymousEventHandler('sysPostHttpError', -- function(event, response, rurl) -- if rurl == url then display(r) else return true end -- end, true) -- -- local url = "https://discordapp.com/api/webhooks/975391849319641161/xVtJk-By84k1Tm9yR3Or8bdMepMInRSufOuVlkxcByX_IEK7rPHvjClFa0zMArSAah7j" -- local data = { -- username = discord_username, -- avatar_url = avatarurl, -- content = message -- } -- local header = { -- ["Content-Type"] = "application/json", -- --["Content-Length"] = data:len() -- } -- -- postHTTP(yajl.to_string(data), url, header) -- -- killAnonymousEventHandler(httpdone) -- killAnonymousEventHandler(httperror) -- end

